  sub _my_tie {
    my ($context, $hash, $options) = @_;
    my $fullopt = $options->{"${context}_CACHE"};
  
    # We already checked to make sure that this works.
    my $shortopt = (ref $fullopt) ? $fullopt->[0] : $fullopt;
    
