  sub TIEHASH {	
    my ($pack) = @_;
    bless \%cache => $pack;
  }
  
  sub EXISTS {
    my ($cache, $key) = @_;
    exists $cache->{$key} ? 1 : 0;
  }
  
  sub FETCH {
    my ($cache, $key) = @_;
    $cache->{$key};
  }
  
  sub STORE {
