  sub first_release_by_date {
      my @perls = &first_release_raw;
      return unless @perls;
      return (sort _released_order @perls)[0];
  }
  
  sub first_release {
      my @perls = &first_release_raw;
      return unless @perls;
      return (sort { $a cmp $b } @perls)[0];
  }
  
  sub removed_from {
    my @perls = &removed_raw;
    return shift @perls;
  }
  
  sub removed_from_by_date {
    my @perls = sort _released_order &removed_raw;
    return shift @perls;
